// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.20;

import {IDepositContract} from "../interfaces/IDepositContract.sol";

/// @title  A contract for holding a eth2 validator withrawal pubkey
/// @author @chimeraDefi
/// @notice A contract for holding a eth2 validator withrawal pubkey
/// @dev Downstream contract needs to implement who can set the withdrawal address and set it
contract ETH2DepositWithdrawalCredentials {
  uint256 internal constant _depositAmount = 32 ether;
  IDepositContract public immutable DEPOSIT_CONTRACT;
  bytes public withdrawalPubKey; // Pubkey for ETH 2.0 withdrawal creds

  event WithdrawalCredentialSet(bytes _withdrawalCredential);

  constructor(address _dc) {
    DEPOSIT_CONTRACT = IDepositContract(_dc);
  }

  /// @notice A more streamlined variant of batch deposit for use with preset withdrawal addresses
  ///         Submit index-matching arrays that form Phase 0 DepositData objects.
  ///         Will create a deposit transaction per index of the arrays submitted.
  ///
  /// @param pubkeys - An array of BLS12-381 public keys.
  /// @param signatures - An array of BLS12-381 signatures.
  /// @param depositDataRoots - An array of the SHA-256 hash of the SSZ-encoded DepositData object.
  function _batchDeposit(
      bytes[] calldata pubkeys,
      bytes[] calldata signatures,
      bytes32[] calldata depositDataRoots
  ) internal {
    // optimizations https://ethereum.stackexchange.com/questions/113221/what-is-the-purpose-of-unchecked-in-solidity
    // https://medium.com/@bloqarl/solidity-gas-optimization-tips-with-assembly-you-havent-heard-yet-1381c77ff078
    // 30m gas / block roughly, say 10m max used so 100 validators a batch max 
    // each deposit call costs roughly 128k https://etherscan.io/tx/0xa2acf6e6bde99b532125cc8026cd88eea345f296968ce732556945ab4705d03e
    uint256 i = pubkeys.length;
    uint256 _amt = _depositAmount;
    bytes memory wpk = withdrawalPubKey;

    while (i > 0) {
      unchecked {
        // While loop check prevents underflow.
        // --i is cheaper than i--
        // reverse while loop cheapest compared to while or for 
        // Since we set the upper loop bound to the arr len, we decr 1st to not hit out of bounds
        --i;

        DEPOSIT_CONTRACT.deposit{value: _amt}(
          pubkeys[i],
          wpk,
          signatures[i],
          depositDataRoots[i]
        );
      }
    }
  }

  /// @notice sets curr_withdrawal_pubkey to be used when deploying validators
  function _setWithdrawalCredential(bytes memory newPk) internal {
    withdrawalPubKey = newPk;

    emit WithdrawalCredentialSet(newPk);
  }
}

contracts/v2/lib/FIFOQueue.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.20;

import {Errors} from "./Errors.sol";

// Simple First in first out queue
// Uses a system of cascading locks based on the block number.
// Users need to wait a minimum of epochLength blocks before withdrawing
// Users past the epoch boundary can claim, allowing some time for earlier users to claim first
abstract contract FIFOQueue {
    struct UserEntry {
        uint256 amount;
        uint256 blocknum;
    }
    mapping(address => UserEntry) public userEntries;

    uint256 public epochLength;

    constructor(uint256 _epochLength) {
        epochLength = _epochLength;
    }

    function _checkWithdraw(
        address sender,
        uint256 balanceOfSelf,
        uint256 amountToWithdraw
    ) public view returns (bool withdrawalAllowed) {
        UserEntry memory ue = userEntries[sender];

        if (!(amountToWithdraw <= balanceOfSelf && amountToWithdraw <= ue.amount)) {
            revert Errors.InvalidAmount();
        }

        if (!(block.number >= ue.blocknum + epochLength)) {
            revert Errors.TooEarly();
        }
        return true;
    }

    function _isWithdrawalAllowed(
        address sender,
        uint256 balanceOfSelf,
        uint256 amountToWithdraw
    ) public view returns (bool) {
        UserEntry memory ue = userEntries[sender];

        return (amountToWithdraw <= balanceOfSelf && amountToWithdraw <= ue.amount) && (block.number >= ue.blocknum + epochLength);
    }

    // should be admin only or used in a constructor upstream
    // set epoch length in blocks
    function _setEpochLength(uint256 _value) internal {
        if (_value == 0) {
            revert Errors.InvalidAmount();
        }
        epochLength = _value;
    }

    function _stakeForWithdrawal(address sender, uint256 amount) internal {
        UserEntry memory ue = userEntries[sender];
        ue.amount = ue.amount + amount;
        ue.blocknum = block.number;
        userEntries[sender] = ue;
    }

    function _withdraw(address sender, uint256 amount) internal {
        UserEntry memory ue = userEntries[sender];
        if (amount > ue.amount) {
            revert Errors.InvalidAmount();
        }

        if (amount == ue.amount) {
            delete userEntries[sender];
        } else {
            ue.amount = ue.amount - amount;
            ue.blocknum = block.number;
            userEntries[sender] = ue;
        }
    }
}

contracts/v2/lib/GranularPause.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.20;
import {Context} from "@openzeppelin/contracts/utils/Context.sol";

/// @title GranularPause 
/// @author ChimeraDefi - chimera_defi@protonmail.com
/// @notice allows more granular control of pausing functions
/// @dev Inherit in child contract, you need to number each function you want to pause

abstract contract GranularPause is Context {
    mapping(uint16 => bool) public paused;

    /**
     * @dev Emitted when the pause is triggered by `account`.
     */
    event Paused(address account, uint16 item);

    /**
     * @dev Emitted when the pause is lifted by `account`.
     */
    event Unpaused(address account, uint16 item);

    error IsPaused();
    error IsNotPaused();

    /**
     * @dev Modifier to make a function callable only when the contract is not paused.
     *
     * Requirements:
     *
     * - The contract must not be paused.
     */
    modifier whenNotPaused(uint16 _id) {
        if (paused[_id]) {
            revert IsPaused();
        }
        _;
    }

    /**
     * @dev Modifier to make a function callable only when the contract is paused.
     *
     * Requirements:
     *
     * - The contract must be paused.
     */
    modifier whenPaused(uint16 _id) {
        if (!paused[_id]) {
            revert IsNotPaused();
        }
        _;
    }

    /// @notice pauses a function
    /// @param _id id of function to pause
    function _pause(uint16 _id) internal virtual {
        paused[_id] = true;
        emit Paused(_msgSender(), _id);
    }

    /// @notice unpauses a function
    /// @param _id id of function to unpause
    function _unpause(uint16 _id) internal virtual {
        paused[_id] = false;
        emit Unpaused(_msgSender(), _id);
    }
}

